import base64 from "Base64";
import qs from "qs";

const { href } = window.location;
const [, params = ""] = href.split("?");
if (params.includes("ers_")) {
  const [, qsString] = params.split("ers_");
  const _params = base64.atob(qsString);
  const { token } = qs.parse(_params);
  localStorage.clear();
  localStorage.access_token = token;
  window.location.replace("/");
}
